import React, { useState } from 'react';
import { Button, Typography, LinearProgress } from '@mui/material';
import CloudUploadIcon from '@mui/icons-material/CloudUpload';
import axios from 'axios';

const FileUpload = ({ setIsFileUploaded }) => {
    const [file, setFile] = useState(null);
    const [uploading, setUploading] = useState(false);
    const [uploadStatus, setUploadStatus] = useState('');

    const handleFileChange = (event) => {
        setFile(event.target.files[0]);
    };

    const handleUpload = async () => {
        if (!file) {
            setUploadStatus('Please select a file first.');
            return;
        }
        const formData = new FormData();
        formData.append('file', file);
        setUploading(true);
        let retries = 3;
        while (retries > 0) {
            try {
                const response = await axios.post('http://localhost:5000/upload', formData, {
                    timeout: 10000 // 10 seconds timeout
                });
                setUploadStatus(response.data.response);
                setIsFileUploaded(true);
                return;
            } catch (error) {
                retries--;
                if (retries === 0) {
                    setUploadStatus(`Upload failed: ${error.message}. Please try again.`);
                } else {
                    setUploadStatus(`Upload attempt failed. Retrying... (${retries} attempts left)`);
                    await new Promise(resolve => setTimeout(resolve, 2000)); // Wait 2 seconds before retrying
                }
            }
        }
        setUploading(false);
    };

    return (
        <div>
            <input
                accept=".pdf,.csv,.xlsx,.docx"
                style={{ display: 'none' }}
                id="raised-button-file"
                type="file"
                onChange={handleFileChange}
            />
            <label htmlFor="raised-button-file">
                <Button variant="contained" component="span" startIcon={<CloudUploadIcon />}>
                    Select File
                </Button>
            </label>
            {file && <Typography>{file.name}</Typography>}
            <Button onClick={handleUpload} disabled={!file || uploading} variant="contained" color="primary">
                Upload
            </Button>
            {uploading && <LinearProgress />}
            <Typography>{uploadStatus}</Typography>
            {uploadStatus.includes('failed') && (
                <Button onClick={handleUpload} variant="contained" color="secondary">
                    Retry Upload
                </Button>
            )}
        </div>
    );
};

export default FileUpload;
